actionConfiguration.body:
() => {
  var today = new Date();
  var mm = today.getMonth() + 1;
  var qq;
  if (mm < 4) qq = 1; else if (mm < 7) qq = 2; else if (mm < 10) qq = 3; else if (mm < 13) qq = 4;
  var dbname;
  var yyyy = today.getFullYear();
  dbname = yyyy + 'q' + qq;
  storeValue('dbname', dbname);
  storeValue('mailsubject', 'Patching Worksheet Step Executed: ' + PatchStepList.selectedItem.step_key + ' By:  ' + appsmith.user.username);
  storeValue('mailbody', 'Patching Alert! Worksheet Step : ' + PatchStepList.selectedItem.step_description + '. Executed By:  ' + appsmith.user.username + 'DB Name:' + dbname);
  LinuxCopyPatchingTables.run();
  InsertPatchStep.run();
  closeModal('Step2');
  showModal('sendmail');
  process_worksheet_vw.run();
}

